        uint256 feeFulfill_,
        address owner,
        address[] memory relayers_,
        uint256 relayerConsensusThreshold_
    ) external initializer {
        _initialize(
            token_,
            limitPerSend_,
            feeWallet_,
            feeSend_,
            feeFulfill_,
            owner,
            relayers_,
            relayerConsensusThreshold_
        );
    }

    function _afterSend(
        address token,
        address user,
        uint256 amount,
        uint256 fee
    ) internal override {
        ITokenMintBurn(token).burn(user, amount);
        if (fee != 0) {
            ITokenMintBurn(token).mint(feeWallet, fee);
        }
    }

    function _afterFulfill(
        address user,
        uint256 amount,
        uint256 fee
    ) internal override {
        address token = TOKEN;
        ITokenMintBurn(token).mint(user, amount - fee);
        if (fee != 0) {
            ITokenMintBurn(token).mint(feeWallet, fee);
        }
    }
}

